package seminar02Animals;public abstract class Animal {    int maxRun;    int maxSwim;    String name;    public static int count;    public Animal(int maxRun, int maxSwim, String name) {        this.maxRun = maxRun;        this.maxSwim = maxSwim;        this.name = name;        count++;    }    public int getMaxRun() {        return maxRun;    }    public void setMaxRun(int maxRun) {        this.maxRun = maxRun;    }    public int getMaxSwim() {        return maxSwim;    }    public void setMaxSwim(int maxSwim) {        this.maxSwim = maxSwim;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public static int getCount() {        return count;    }    public static void setCount(int count) {        Animal.count = count;    }    abstract void running(int runDistance);    abstract void swimming(int swimDistance);}